{
  "openapi": "3.0.1",
  "info": {
    "title": "MAFlyDoc.WebApi",
    "version": "1.0"
  },
  "paths": {
    "/v1/Envois": {
      "post": {
        "tags": [
          "Envois"
        ],
        "summary": "Permet l'envoi d'un ou de plusieurs documents issu(s) de la GED",
        "parameters": [
          {
            "name": "recipientAddressIdLocation",
            "in": "query",
            "description": "<br>Identifie quelle adresse sera choisie\r\n<br>AddressIdProperty: un identifiant technique AdresseId venant de VIGI\r\n<br>AddressIdFromCompteId: l'adresse rattachée au compte dans VIGI",
            "required": true,
            "schema": {
              "allOf": [
                {
                  "$ref": "#/components/schemas/RecipientAddressIdLocation"
                }
              ],
              "description": "Identifie quelle adresse sera choisie"
            }
          }
        ],
        "requestBody": {
          "description": "",
          "content": {
            "application/json; x-api-version=1.0": {
              "schema": {
                "allOf": [
                  {
                    "$ref": "#/components/schemas/EnvoiCommand"
                  }
                ],
                "description": "Commande d'envoi de courrier"
              }
            },
            "text/json; x-api-version=1.0": {
              "schema": {
                "allOf": [
                  {
                    "$ref": "#/components/schemas/EnvoiCommand"
                  }
                ],
                "description": "Commande d'envoi de courrier"
              }
            },
            "application/*+json; x-api-version=1.0": {
              "schema": {
                "allOf": [
                  {
                    "$ref": "#/components/schemas/EnvoiCommand"
                  }
                ],
                "description": "Commande d'envoi de courrier"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Created"
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      }
    },
    "/v1/Envois/Envoi-avec-adresse-du-destinataire-en-clair": {
      "post": {
        "tags": [
          "Envois"
        ],
        "summary": "Permet l'envoi d'un ou de plusieurs documents issu(s) de la GED à une adresse de destinataire en clair",
        "requestBody": {
          "content": {
            "application/json; x-api-version=1.0": {
              "schema": {
                "allOf": [
                  {
                    "$ref": "#/components/schemas/EnvoiAvecAdresseDuDestinataireEnClairCommand"
                  }
                ],
                "description": "Commande d'envoi de courrier"
              }
            },
            "text/json; x-api-version=1.0": {
              "schema": {
                "allOf": [
                  {
                    "$ref": "#/components/schemas/EnvoiAvecAdresseDuDestinataireEnClairCommand"
                  }
                ],
                "description": "Commande d'envoi de courrier"
              }
            },
            "application/*+json; x-api-version=1.0": {
              "schema": {
                "allOf": [
                  {
                    "$ref": "#/components/schemas/EnvoiAvecAdresseDuDestinataireEnClairCommand"
                  }
                ],
                "description": "Commande d'envoi de courrier"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Created"
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      }
    },
    "/v1/Envois/{envoi-id}": {
      "get": {
        "tags": [
          "Envois"
        ],
        "summary": "Permet de récupérer les informations d'un envoi avec l'identifiant d'envoi MAF",
        "parameters": [
          {
            "name": "envoi-id",
            "in": "path",
            "description": "identifiant d'envoi MAF",
            "required": true,
            "schema": {
              "type": "integer",
              "format": "int32"
            }
          },
          {
            "name": "with-etat-envoi-history",
            "in": "query",
            "description": "détermine si les données retournées doivent comporter l'historique des états de l'envoi",
            "schema": {
              "type": "boolean",
              "default": false
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/EnvoiQueryResult"
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/EnvoiQueryResult"
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/EnvoiQueryResult"
                }
              }
            }
          }
        }
      }
    },
    "/v1/Envois/Envois-from-envois-id-list": {
      "get": {
        "tags": [
          "Envois"
        ],
        "summary": "Permet de récupérer un ensemble d'envois d'après une liste d'identifiants d'envois MAF sépaprés par des virgules",
        "parameters": [
          {
            "name": "comma-separated-envois-id-list",
            "in": "query",
            "description": "liste d'identifiants d'envois MAF sépaprés par des virgules",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "with-etat-envoi-history",
            "in": "query",
            "description": "détermine si les données retournées doivent comporter l'historique des états de l'envoi",
            "schema": {
              "type": "boolean",
              "default": false
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EnvoiQueryResult"
                  }
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EnvoiQueryResult"
                  }
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EnvoiQueryResult"
                  }
                }
              }
            }
          }
        }
      }
    },
    "/v1/Envois/Synchronisation": {
      "post": {
        "tags": [
          "Envois"
        ],
        "summary": "Récupérer l'état des envois en cours depuis le web service Esker, et les enregistrer en base de données",
        "responses": {
          "202": {
            "description": "Accepted"
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      },
      "get": {
        "tags": [
          "Envois"
        ],
        "summary": "Retourne l'état de l'opération déclenchable par requête POST au point de terminaison \"Synchronisation\"",
        "responses": {
          "200": {
            "description": "Success"
          }
        }
      }
    },
    "/v1/Envois/Relance": {
      "post": {
        "tags": [
          "Envois"
        ],
        "summary": "Faire une nouvelle tentative pour les envois dont l'initalisation à échouée auprès du web service Esker,\r\net pour lesquels le nombre de tentatives restantes est supérieur à 0",
        "responses": {
          "202": {
            "description": "Accepted"
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      },
      "get": {
        "tags": [
          "Envois"
        ],
        "summary": "Retourne l'état de l'opération déclenchable par requête POST au point de terminaison \"Relance\"",
        "responses": {
          "200": {
            "description": "Success"
          }
        }
      }
    },
    "/v1/Envois/Telecharger-documents-accuse-de-reception": {
      "post": {
        "tags": [
          "Envois"
        ],
        "summary": "Télécharge les documents et les enregistre dans la web API GED, pour les envois avec\r\naccusé de réception dont les documents n'ont pas déjà été téléchargés.",
        "responses": {
          "202": {
            "description": "Accepted"
          },
          "400": {
            "description": "Bad Request",
