              "text/plain; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/ProblemDetails"
                }
              }
            }
          }
        }
      },
      "get": {
        "tags": [
          "Envois"
        ],
        "summary": "Retourne l'état de l'opération déclenchable par requête POST au point de terminaison \"Telecharger-documents-accuse-de-reception\"",
        "responses": {
          "200": {
            "description": "Success"
          }
        }
      }
    },
    "/v1/Envois/Annuler-envois-from-envois-id-list": {
      "post": {
        "tags": [
          "Envois"
        ],
        "summary": "Envoyer une commande de tentative d'annulation d'envois d'après une liste d'identifiants d'envois MAF sépaprés par des virgules",
        "parameters": [
          {
            "name": "comma-separated-envois-id-list",
            "in": "query",
            "description": "liste d'identifiants d'envois MAF sépaprés par des virgules",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/EnvoisListCancelationCommandResult"
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/EnvoisListCancelationCommandResult"
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "$ref": "#/components/schemas/EnvoisListCancelationCommandResult"
                }
              }
            }
          }
        }
      }
    },
    "/v1/Envois/Abandonner-envoi/{envoi-id}": {
      "post": {
        "tags": [
          "Envois"
        ],
        "parameters": [
          {
            "name": "envoi-id",
            "in": "path",
            "required": true,
            "schema": {
              "type": "integer",
              "format": "int32"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success"
          }
        }
      }
    },
    "/v1/Envois/Recherche-envois": {
      "get": {
        "tags": [
          "Envois"
        ],
        "parameters": [
          {
            "name": "MailPostages",
            "in": "query",
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/MailPostage"
              }
            }
          },
          {
            "name": "EtatsEnvoiActuel",
            "in": "query",
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/EtatEnvoiEnum"
              }
            }
          },
          {
            "name": "DateCreationEnvoi.From",
            "in": "query",
            "schema": {
              "type": "string",
              "format": "date"
            }
          },
          {
            "name": "DateCreationEnvoi.To",
            "in": "query",
            "schema": {
              "type": "string",
              "format": "date"
            }
          },
          {
            "name": "with-etat-envoi-history",
            "in": "query",
            "schema": {
              "type": "boolean",
              "default": false
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain; x-api-version=1.0": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EnvoiQueryResult"
                  }
                }
              },
              "application/json; x-api-version=1.0": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EnvoiQueryResult"
                  }
                }
              },
              "text/json; x-api-version=1.0": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EnvoiQueryResult"
                  }
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "DocumentsArTelechargesGedIdQueryResult": {
        "type": "object",
        "properties": {
          "preuveDeDepotGedId": {
            "type": "string",
            "nullable": true
          },
          "accuseReceptionGedId": {
            "type": "string",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "EnvoiAvecAdresseDuDestinataireEnClairCommand": {
        "required": [
          "adresseDuDestinataireLignes",
          "mainDocumentGedId",
          "sender"
        ],
        "type": "object",
        "properties": {
          "sender": {
            "allOf": [
              {
                "$ref": "#/components/schemas/SenderEnvoiCommand"
              }
            ],
            "description": "Defini les informations de l'expediteur du courrier"
          },
          "adresseDuDestinataireLignes": {
            "type": "array",
            "items": {
              "type": "string"
            }
          },
          "mainDocumentGedId": {
            "type": "string",
            "description": "L'identifiant GED (GED ID) du document document principal"
          },
          "attachementsGedIdList": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Un tableau d'identifiants GED (GED ID) représentant les pièces jointes",
            "nullable": true
          },
          "mailPostage": {
            "allOf": [
              {
                "$ref": "#/components/schemas/MailPostage"
              }
            ],
            "description": "Niveau de priorité de l'envoi du courrier"
          },
          "subject": {
            "type": "string",
            "description": "Objet du courrier. N'apparait pas dans le courrier mais permet de retrouver le courrier dans l'interface Flydoc (champ Objet)",
            "nullable": true
          },
          "impression": {
            "allOf": [
              {
                "$ref": "#/components/schemas/Impression"
              }
            ],
            "description": "Impression du courrier recto seul ou recto verso",
            "nullable": true
          },
          "application": {
            "type": "string",
            "description": "Application émettrice du courrier",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Commande d'envoi de courrier"
      },
      "EnvoiCancelationError": {
        "type": "object",
        "properties": {
          "envoiId": {
            "type": "integer",
            "description": "identifiant MAF de l'envoi",
            "format": "int32"
          },
          "error": {
            "type": "string",
            "description": "motif de rejet de la tentative d'annulation",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "résultat d'une tentive d'annulation d'envoi qui a été rejetée"
      },
      "EnvoiCommand": {
        "required": [
          "mainDocumentGedId",
          "recipient",
          "sender"
        ],
        "type": "object",
        "properties": {
          "sender": {
            "allOf": [
              {
                "$ref": "#/components/schemas/SenderEnvoiCommand"
              }
            ],
            "description": "Defini les informations de l'expediteur du courrier"
          },
          "recipient": {
            "allOf": [
              {
                "$ref": "#/components/schemas/RecipientEnvoiCommand"
              }
            ],
            "description": "décrit le destinataire du courrier"
          },
          "mainDocumentGedId": {
            "type": "string",
            "description": "L'identifiant GED (GED ID) du document document principal"
          },
          "attachementsGedIdList": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Un tableau d'identifiants GED (GED ID) représentant les pièces jointes",
            "nullable": true
          },
          "mailPostage": {
            "allOf": [
              {
                "$ref": "#/components/schemas/MailPostage"
              }
            ],
            "description": "Niveau de priorité de l'envoi du courrier"
          },
          "subject": {
            "type": "string",
            "description": "Objet du courrier. N'apparait pas dans le courrier mais permet de retrouver le courrier dans l'interface Flydoc (champ Objet)",
            "nullable": true
          },
          "impression": {
            "allOf": [
              {
                "$ref": "#/components/schemas/Impression"
              }
            ],
            "description": "Impression du courrier recto seul ou recto verso",
            "nullable": true
          },
          "application": {
            "type": "string",
            "description": "Application émettrice du courrier",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Commande d'envoi de courrier"
      },
      "EnvoiQueryResult": {
        "type": "object",
        "properties": {
          "envoiId": {
            "type": "integer",
            "description": "Idenfifiant de l'envoi",
            "format": "int32"
          },
          "sender": {
            "allOf": [
              {
                "$ref": "#/components/schemas/SenderEnvoiQueryResult"
              }
            ],
            "description": "Expéditeur du courrier",
            "nullable": true
          },
          "recipient": {
            "allOf": [
              {
                "$ref": "#/components/schemas/RecipientEnvoiQueryResult"
              }
            ],
            "description": "Destinataire auquel le courrier a été envoyé",
            "nullable": true
          },
          "subject": {
            "type": "string",
            "description": "Objet du courrier",
            "nullable": true
          },
          "mailPostage": {
            "allOf": [
              {
                "$ref": "#/components/schemas/MailPostage"
              }
            ],
            "description": "Type d'affranchissement"
          },
          "transportId": {
            "type": "string",
            "description": "Identifiant de l'envoi dans le service Esker On Demand",
            "nullable": true
          },
          "submissionId": {
            "type": "string",
            "description": "Identifiant de la requête de création d'envoi dans le service Esker On Demand",
            "nullable": true
          },
          "documentsArTelechargesGedId": {
            "allOf": [
              {
                "$ref": "#/components/schemas/DocumentsArTelechargesGedIdQueryResult"
              }
            ],
            "description": "Identifiants GED des documents générés suite à un envoi en recommandé avec accusé de réception",
            "nullable": true,
            "readOnly": true
          },
          "etatsEnvoiHistoryEntriesList": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/EtatEnvoiHistoryEntry"
            },
            "description": "Liste des états par lesquels est passé l'envoi plus son état actuel",
            "nullable": true
          },
          "lastEtatEnvoiHistoryEntry": {
            "allOf": [
              {
                "$ref": "#/components/schemas/EtatEnvoiHistoryEntry"
              }
            ],
            "description": "Dernier état connu de l'envoi",
            "nullable": true
          },
          "etatFinalErrorMessage": {
            "type": "string",
            "description": "Détail d'erreur pour un état final en erreur",
            "nullable": true
          },
          "impression": {
            "allOf": [
              {
                "$ref": "#/components/schemas/Impression"
              }
            ],
            "description": "Côtés des feuills utilisés pour l'impression",
            "nullable": true
          }
        },
        "additionalProperties": false
      },
      "EnvoisListCancelationCommandResult": {
        "type": "object",
        "properties": {
          "envoiIdSuccessfullyCanceledList": {
            "type": "array",
            "items": {
              "type": "integer",
              "format": "int32"
            },
            "description": "liste des envois dont la tentative d'annulation a été prise en compte",
            "nullable": true
          },
          "cancelationErrors": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/EnvoiCancelationError"
            },
            "description": "liste des envois dont la tentative d'annulation a été rejetée",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "résultat d'une commande de tentative d'annulation d'envois"
      },
      "EtatEnvoiEnum": {
        "enum": [
          "EN_COURS_D_ENVOI",
          "EN_COURS_DE_TRAITEMENT",
          "ENVOYE",
          "NON_DISTRIBUE",
          "REMIS_AU_DESTINATAIRE",
          "TRAITEMENT_ECHOUE",
          "TRAITEMENT_ANNULE",
          "TRAITEMENT_REJETE",
          "ENVOI_ABANDONNE",
          "ABANDONNE_PAR_LA_MAF"
        ],
        "type": "string"
      },
      "EtatEnvoiHistoryEntry": {
        "type": "object",
        "properties": {
          "etat": {
            "allOf": [
              {
                "$ref": "#/components/schemas/EtatEnvoiEnum"
              }
            ]
          },
          "dateTime": {
            "type": "string",
            "format": "date-time"
          }
        },
